import React from 'react';
import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonGrid, IonRow, IonButton } from '@ionic/react';
import './Tab1.css';
import { Chore } from '../../interfaces/chore-interface';

interface Props {
  onChange: (event: React.ChangeEvent<HTMLInputElement>) => void;
  onAdd: (event: React.FormEvent<HTMLFormElement>) => void;
  chore: Chore;
}

const Tab1: React.FC<Props> = ({ onChange, onAdd }) => {
  return (
    <IonPage>
      <IonContent class="page-content">
        <IonContent class="header-content">
          <IonHeader class="header-container">
            <IonToolbar>
              <IonTitle class="tab-title" size="large">Track your daily chores here!</IonTitle>
            </IonToolbar>
          </IonHeader>
          <form onSubmit={onAdd}>
            <IonGrid>
                <IonRow>
                  <input placeholder='Enter new chore here:' onChange={onChange}/>
                </IonRow> <br/>
                <IonRow>
                  <IonButton fill='outline' type='submit'> Confirm </IonButton>
                </IonRow>
            </IonGrid>
          </form>
        </IonContent>
      </IonContent>
    </IonPage>
  );
};

export default Tab1;
